Bash(powershell -File - <<'EOF'
$port = New-Object System.IO.Ports.SerialPort "COM5", 115200
$port.ReadTimeout = 5000
try {
    $port.Open\(\)
    Start-Sleep -Milliseconds 1000
    while \($true\) {
        try {
            $line = $port.ReadLine\(\)
            Write-Host $line
        } catch {
            break
        }
    }
    $port.Close\(\)
} catch {
    Write-Host "Error: $_"
}
EOF)
